import React, { Component } from "react";
import { Map, TileLayer } from "react-leaflet";
import LocateControl from "../LocateControl";
import Control from "react-leaflet-control";
import { FaSearch } from "react-icons/fa";

import CardForm from '../CardForm';
import "./Map.css";

//import {gpsDirectionMarker, positionMarker} from '../Markers';
//import 'leaflet-rotatedmarker';

//Options for project search
/*
const projectOptions = [
  {value: [63.551440, 10.933473], label: 'E6 Kvithammar - Åsen'},
  {value: [58.093886,7.644329], label: 'E39 Mandal'},
  {value: [59.001439, 9.613338], label: 'E18 Rugtvedt Dørdal'}
  //Legge til resterende
];*/

export default class Kart extends Component {
  state = {
    location: {
      lat: 63.430515,
      lng: 10.395053,
    },
    positionCoord: {
      lat: 0,
      lng: 0,
    },
    haveUsersLocation: false,
    zoom: 5,
    showHideCard: true,
  };

  showHideComponent = () => {
    console.log(this.state.showHideCard);
    this.setState({ showHideCard: !this.state.showHideCard });
  }

  render() {
    const position = [this.state.location.lat, this.state.location.lng];
    const showHideCard = this.setState;

    const locateOptions = {
      position: "topleft",
      strings: {
        title: "Vis posisjon min",
      },
      onActive: () => {}, // callback before engine starts retrieving locations
    };

    return (
      <div className="app">
        <Map
          className="map"
          center={position}
          zoom={this.state.zoom}
          zoomControl={false}
        >
          <TileLayer
            attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <LocateControl options={locateOptions} />
          <Control position="topright">
            <button className="custom-btn" onClick={() => this.showHideComponent()}>
              <FaSearch />
            </button>
          </Control>
          {showHideCard ? <CardForm/>: null}
        </Map>
      </div>
    );
  }
}
